    private void tTerminalKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_tTerminalKeyPressed

        if (evt.getKeyCode() == 87) {
           
            System.out.println("andando para frente");
       
        } else if (evt.getKeyCode() == 83) {
           
            System.out.println("andando para tr√°s");
        
        } else if (evt.getKeyCode() == 68) {
           
            System.out.println("andando para direita");
        
        } else if(evt.getKeyCode() == 65) {
            
            System.out.println("andando para esquerda");
        }

    }//GEN-LAST:event_tTerminalKeyPressed

